import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import { Product } from '../product-list/product.model';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private apiUrl = 'http://localhost:3002/products';
  public cartAddedSubject = new Subject<boolean>();


  constructor(private http: HttpClient) { }

  getProducts(): Observable<any[]> {
    return this.http.get<any[]>(this.apiUrl);
  }
  getProduct(id: number): Observable<Product> {
    return this.http.get<Product>(`http://localhost:3002/productsById/${id}`);
  }

  addToCart(id: number) : Observable<Product[]>  {
    console.log("fggggh",id);
    return this.http.get<Product[]>(`http://localhost:3002/addToCart/${id} `);
  }

  getCartItemsByCustId(id: number) : Observable<any[]>  {
    return this.http.get<any[]>(`http://localhost:3002/addToCart/${id} ` + id);
  }
   removeCartItemById(cartId: number) : Observable<any[]>  {
    return this.http.get<any[]>("http://onlinetestapi.gerasim.in/api/Ecomm/DeleteProductFromCartById?id=" + cartId);
  }

  makeSale(obj: any) : Observable<any> {
    return this.http.post<any>("http://onlinetestapi.gerasim.in/api/Ecomm/AddNewSale",obj);
  }
}
