import { Component, OnInit } from '@angular/core';
import { Subscription } from 'rxjs';
import { Product } from 'src/app/product-list/product.model';
import { ProductService } from 'src/app/services/product.service';



@Component({
  selector: 'app-sale',
  templateUrl: './sale.component.html',
  styleUrls: ['./sale.component.css']
})
export class SaleComponent implements OnInit {
  products:Product[]=[];
  cartItems: Product[] = [];
  cartProducts: Product[] = [];
  totalItems:Number=0;
  totalPrice:Number=0;

  private cartItemsSubscription:Subscription=new Subscription;


  constructor(private productService:ProductService) { }

  ngOnInit(){
    this.cartItemsSubscription=this.productService.cartItems$.subscribe((cartItems)=>{
      this.cartItems=cartItems;
      this.calculateTotals();
    },
    (error)=>{
      console.error('error fetching cart items:', error);
    }
  );
  this.productService.fetchCartItems();
  }
  ngOnDestroy():void{
    this.cartItemsSubscription.unsubscribe();
  }
  calculateTotals():void {
    this.totalItems=this.cartItems.length;
    this.totalPrice=this.cartItems.reduce((acc,item)=>acc + (item.quantity * item.price), 0);
  }
