import { Product } from 'src/app/product-list/product.model';
import { ProductService } from 'src/app/services/product.service';


@Component({
  selector: 'app-sale',
  templateUrl: './sale.component.html',
  styleUrls: ['./sale.component.css']
})
export class SaleComponent implements OnInit {
  products:Product[]=[];
  cartItems: Product[] = []; // Replace any with your actual item type

  constructor(private productService:ProductService) { }

  ngOnInit(){
    // this.productService.fetchProductsById().subscribe((data:Product[])=>{
    //   this.products=data;
    // });

  }



  // Example method to remove item from cart
  fetchProductsById(id:number){
    this.productService.fetchCartItems().subscribe((data:Product[])=>{
      this.cartItems=data;
    });
  }
}
