import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
// import { BehaviorSubject } from 'rxjs';
import { Product } from '../product-list/product.model';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class SharedService {
  // private messageSource=new BehaviorSubject<Product[]>([]);

  // currentValue=this.messageSource.asObservable();
  // constructor() { }
  // changeValue(product:any){
  //   console.log("line number 14",product)
  //   this.messageSource.next(product);

  // }
  private apiUrl = '/http://localhost:3000/products';
  constructor(private http:HttpClient){}


  getProducts(): Observable<Product[]> {
    return this.http.get<Product[]>(`${this.apiUrl}/product`);
  }

  getProduct(productId: number): Observable<Product> {
    return this.http.get<Product>(`${this.apiUrl}/product/${productId}`);
  }
  addToCart(product: Product): void {
    // Assuming you have some cart management logic here
    let cart = JSON.parse(localStorage.getItem('cart') || '[]');
    cart.push(product);
    localStorage.setItem('cart', JSON.stringify(cart));
  }
}
